import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";

export async function registerRoutes(app: Express): Promise<Server> {
  // Mock user for development
  const mockUser = {
    id: "user-123",
    username: "demo_user",
    email: "demo@example.com",
    first_name: "Demo",
    last_name: "User",
    age: 25,
    bio: "Demo user for testing",
    profile_image_url: "",
    points: 150,
    level: 2
  };

  // Authentication routes
  app.get("/api/auth/me", (req, res) => {
    // Return mock authenticated user
    res.json(mockUser);
  });

  // User routes
  app.get("/api/users/:id", (req, res) => {
    const { id } = req.params;
    if (id === mockUser.id) {
      res.json(mockUser);
    } else {
      res.status(404).json({ error: "User not found" });
    }
  });

  // Skills routes
  app.get("/api/skills", (req, res) => {
    // Return mock skills data
    res.json([
      { id: "1", name: "JavaScript", category: "Programming" },
      { id: "2", name: "React", category: "Frontend" },
      { id: "3", name: "Node.js", category: "Backend" }
    ]);
  });

  // Skill matches routes
  app.get("/api/skill-matches/:userId", (req, res) => {
    res.json([]);
  });

  app.get("/api/skill-matches/percentiles", (req, res) => {
    res.json({});
  });

  // Gamification routes
  app.get("/api/gam/state/:userId", (req, res) => {
    const { userId } = req.params;
    if (userId === mockUser.id) {
      res.json({
        id: userId,
        username: mockUser.username,
        level: mockUser.level,
        xp: mockUser.points,
        streak: 3,
        badges: ["Welcome Badge", "First Level"],
        skills: ["JavaScript", "React"]
      });
    } else {
      res.status(404).json({ error: "Gamification state not found" });
    }
  });

  app.post("/api/gam/earn", (req, res) => {
    const { points } = req.body;
    res.json({ 
      success: true, 
      message: `Earned ${points} points!`,
      newTotal: mockUser.points + points 
    });
  });

  // Meeting rooms routes
  app.get("/api/meeting-rooms", (req, res) => {
    res.json([]);
  });

  app.post("/api/meeting-rooms", (req, res) => {
    const roomData = req.body;
    res.json({ 
      id: "room-123", 
      ...roomData,
      created_at: new Date().toISOString()
    });
  });

  app.post("/api/meeting-rooms/:roomId/join", (req, res) => {
    const { roomId } = req.params;
    res.json({ 
      success: true, 
      message: `Joined room ${roomId}`,
      room_id: roomId
    });
  });

  // Sync routes
  app.post("/api/sync-zoom", (req, res) => {
    res.json({ synced: [] });
  });

  const httpServer = createServer(app);

  return httpServer;
}

