  }

  @keyframes pulseBorder {
    0%, 100% { border-color: var(--primary); box-shadow: 0 0 5px var(--primary); }
    50% { border-color: var(--secondary); box-shadow: 0 0 20px var(--secondary), 0 0 30px var(--secondary); }
  }
}

