import React, { useEffect, useState } from "react";
import { io, Socket } from "socket.io-client";
import { motion } from "framer-motion";
import { Button } from "@/components/ui/button";
import { useQuery } from "@tanstack/react-query";

export type UserProfile = {
  id: string;
  username: string;
  level: number;
  xp: number;
  streak: number;
  badges: string[];
  skills: string[];
};

export type LeaderboardEntry = {
  userId: string;
  username: string;
  score: number;
  level: number;
};

const SOCKET_URL = import.meta.env.VITE_GAMIFY_SOCKET ?? "http://localhost:5000";
const USE_MOCK = true; // Force mock mode for demo purposes

function xpNeededForLevel(level: number) {
  return Math.floor(500 * Math.pow(1.25, level - 1));
}

function applyDailyBonus(user: UserProfile) {
  const bonus = 50 * user.streak;
  return { ...user, xp: user.xp + bonus };
}

export function useGamification(userId: string) {
  const [socket, setSocket] = useState<Socket | null>(null);
  const [profile, setProfile] = useState<UserProfile | null>(null);
  const [leaderboard, setLeaderboard] = useState<LeaderboardEntry[]>([]);
  const [notifications, setNotifications] = useState<string[]>([]);
  const [cooldown, setCooldown] = useState(false);

  useEffect(() => {
    if (USE_MOCK) {
      setProfile({
        id: userId,
        username: "DemoUser",
        level: 1,
        xp: 0,
        streak: 1,
        badges: [],
        skills: ["Math", "Science"],
      });
      setLeaderboard([]);
      return;
    }

    const s = io(SOCKET_URL, { transports: ["websocket"] });
    setSocket(s);

    s.on("profileUpdate", (data: UserProfile) => setProfile(data));
    s.on("leaderboardUpdate", (lb: LeaderboardEntry[]) => setLeaderboard(lb));
    s.on("notification", (msg: string) => pushNotif(msg));

    s.emit("getProfile", userId);

    return () => {
      s.disconnect();
    };
  }, [userId]);

  function pushNotif(msg: string) {
    setNotifications((prev) => [msg, ...prev].slice(0, 5));
  }

  function earnPoints(amount: number) {
    if (cooldown) {
      pushNotif("â³ Please wait before earning again!");
      return;
    }
    setCooldown(true);
    setTimeout(() => setCooldown(false), 3000);

    if (USE_MOCK) {
      if (!profile) return;
      const updated = { ...profile, xp: profile.xp + amount };
      const xpThreshold = xpNeededForLevel(updated.level);
      if (updated.xp >= xpThreshold) {
        updated.level++;
        updated.xp -= xpThreshold;
        updated.badges.push(`Level ${updated.level} Unlocked`);
        pushNotif(`ðŸŽ‰ You leveled up to ${updated.level}!`);
      }
      setProfile(updated);
      return;
    }

    if (socket) socket.emit("earnPoints", { userId, amount });
  }

  function claimDailyBonus() {
    if (!profile) return;
    if (USE_MOCK) {
      const updated = applyDailyBonus(profile);
      pushNotif(`ðŸ”¥ Daily streak bonus applied! (+${50 * profile.streak})`);
      setProfile(updated);
      return;
    }
    if (socket) socket.emit("claimDaily", { userId });
  }

  return { profile, leaderboard, notifications, earnPoints, claimDailyBonus, cooldown };
}

export function GamificationTab({ userId }: { userId: string }) {
  const { profile, leaderboard, notifications, earnPoints, claimDailyBonus, cooldown } =
