import { useState } from "react";

//  Raw skill list for dropdowns
export const skills = [
  "Gardening", "Cooking", "Baking", "Reading", "Writing", "Drawing", "Painting", "Sculpting",
  "Music (Playing Instrument)", "Singing", "Dancing", "Photography", "Videography", "Coding",
  "Web Development", "Mobile App Development", "Game Development", "Academics (General)",
  "History", "Science", "Mathematics", "Literature", "Foreign Languages", "Tutoring", "Mentoring",
  "Sports (Team)", "Sports (Individual)", "Yoga", "Meditation", "Hiking", "Camping", "Fishing",
  "Board Games", "Card Games", "Puzzles", "Knitting", "Crocheting", "Sewing", "Woodworking",
  "Pottery", "Calligraphy", "Graphic Design", "Animation", "Creative Writing", "Poetry",
  "Volunteering", "Community Service", "First Aid", "CPR", "Animal Care", "Event Planning",
  "Public Speaking", "Storytelling", "Astronomy", "Bird Watching", "Genealogy", "Home Improvement",
  "Car Repair", "Financial Literacy", "Investing",
];

// Structured skill entry for profile
export interface SkillEntry {
  name: string;
  proficiency: number; // 1â€“5
  yearsExperience: number;
  wantsToTeach: boolean;
  wantsToLearn: boolean;
}

export function useSkills() {
  const [skillEntries, setSkillEntries] = useState<SkillEntry[]>([]);

  const addSkill = (entry: SkillEntry) => {
    setSkillEntries((prev) => {
      const exists = prev.find((s) => s.name === entry.name);
      return exists
        ? prev.map((s) => (s.name === entry.name ? entry : s))
        : [...prev, entry];
    });
  };

  const removeSkill = (skillName: string) => {
    setSkillEntries((prev) => prev.filter((s) => s.name !== skillName));
  };

  return {
    skills,
    skillEntries,
    addSkill,
    removeSkill,
  };
}

client/src/pages/gamification-tab.tsx.content:
import React, { useEffect, useState } from "react";
import { motion } from "framer-motion";
import { Button } from "@/components/ui/button";

export type UserProfile = {
  id: string;
  username: string;
  level: number;
  xp: number;
  streak: number;
  badges: string[];
  skills: string[];
};

export type LeaderboardEntry = {
  userId: string;
  username: string;
  score: number;
  level: number;
};

function xpNeededForLevel(level: number) {
  return Math.floor(500 * Math.pow(1.25, level - 1));
}

function applyDailyBonus(user: UserProfile): UserProfile {
  const bonus = 50 * user.streak;
  return {
    ...user,
    xp: user.xp + bonus,
    streak: user.streak + 1,
  };
}

export default function GamificationTab({ userId }: { userId: string }) {
  const [profile, setProfile] = useState<UserProfile>({
    id: userId,
    username: "DemoUser",
    level: 1,
    xp: 0,
    streak: 1,
    badges: [],
    skills: ["Math", "Science"],
  });

  const [leaderboard, setLeaderboard] = useState<LeaderboardEntry[]>([]);
  const [notifications, setNotifications] = useState<string[]>([]);
  const [cooldown, setCooldown] = useState(false);

  useEffect(() => {
    updateLeaderboard(profile);
  }, []);

  function pushNotif(msg: string) {
    setNotifications((prev) => [msg, ...prev].slice(0, 5));
  }

  function updateLeaderboard(p: UserProfile) {
    const entry: LeaderboardEntry = {
      userId: p.id,
      username: p.username,
      score: p.xp + p.level * 100,
      level: p.level,
    };
    setLeaderboard([entry]);
  }

  function earnPoints(amount: number) {
    if (cooldown) {
      pushNotif("â³ Please wait before earning again!");
      return;
    }
    setCooldown(true);
    setTimeout(() => setCooldown(false), 3000);

    let updated = { ...profile, xp: profile.xp + amount };
    const xpThreshold = xpNeededForLevel(updated.level);

    if (updated.xp >= xpThreshold) {
      updated.level++;
      updated.xp -= xpThreshold;
      updated.badges.push(`Level ${updated.level} Unlocked`);
      pushNotif(`ðŸŽ‰ You leveled up to ${updated.level}!`);
    }

    setProfile(updated);
    updateLeaderboard(updated);
  }

  function claimDailyBonus() {
    const updated = applyDailyBonus(profile);
    pushNotif(`ðŸ”¥ Daily streak bonus applied! (+${50 * profile.streak})`);
    setProfile(updated);
    updateLeaderboard(updated);
  }

  const xpNeeded = xpNeededForLevel(profile.level);
  const progress = Math.min((profile.xp / xpNeeded) * 100, 100);

  return (
    <section id="gamify" className="py-20 bg-background font-arcade">
      <div className="max-w-6xl mx-auto px-6">
        <h2 className="text-3xl text-primary text-center mb-6">ðŸ† Gamification</h2>

        {/* XP + LEVEL */}
        <div className="bg-card p-6 rounded-lg shadow-md mb-6 text-center">
          <h3 className="text-xl mb-2">Level {profile.level}</h3>
          <motion.div
            initial={{ width: 0 }}
            animate={{ width: `${progress}%` }}
            transition={{ duration: 0.8 }}
            className="h-4 bg-primary rounded-full"
          />
          <p className="text-sm mt-2">
            {profile.xp} / {xpNeeded} XP ({progress.toFixed(1)}%)
          </p>
        </div>

        {/* Buttons */}
        <div className="flex gap-4 justify-center mb-6">
          <Button
            onClick={() => earnPoints(120)}
            className="bg-primary px-6"
            disabled={cooldown}
          >
            {cooldown ? "Cooldown..." : "Earn Points"}
          </Button>
          <Button onClick={claimDailyBonus} className="bg-accent px-6">
            Claim Daily Bonus
          </Button>
        </div>

        {/* Streaks & Badges */}
        <div className="grid grid-cols-2 gap-4 mb-6">
          <div className="bg-card p-4 rounded">
            <h4 className="text-lg mb-2">ðŸ”¥ Streak</h4>
            <p>{profile.streak} days</p>
            <p className="text-xs text-muted-foreground">
              Bonus: +{50 * profile.streak} XP
            </p>
          </div>
          <div className="bg-card p-4 rounded">
            <h4 className="text-lg mb-2">ðŸŽ–ï¸ Badges</h4>
            <ul className="text-sm list-disc pl-4">
              {profile.badges.length ? (
                profile.badges.map((b, i) => <li key={i}>{b}</li>)
              ) : (
                <li>No badges yet</li>
              )}
            </ul>
          </div>
        </div>

        {/* Leaderboard */}
        <div className="bg-card p-6 rounded">
          <h4 className="text-lg mb-3">Leaderboard</h4>
          {leaderboard.length === 0 ? (
            <p className="text-sm text-muted-foreground">No leaderboard data yet</p>
          ) : (
            <ul>
              {leaderboard.map((entry, i) => (
                <li
                  key={entry.userId}
                  className={`flex justify-between ${
                    entry.userId === profile.id ? "bg-primary/20 font-bold" : ""
                  } p-2 rounded`}
                >
                  <span>
                    {i === 0 ? "ðŸ¥‡" : i === 1 ? "ðŸ¥ˆ" : i === 2 ? "ðŸ¥‰" : `#${i + 1}`} {entry.username}
                  </span>
                  <span>Lv {entry.level} ({entry.score} pts)</span>
                </li>
              ))}
            </ul>
          )}
        </div>

        {/* Notifications */}
        <div className="mt-6">
          <h4 className="text-lg mb-2">Notifications</h4>
          <ul className="text-sm text-muted-foreground">
            {notifications.length ? (
              notifications.map((n, i) => <li key={i}>â€¢ {n}</li>)
            ) : (
              <li>No recent activity</li>
            )}
          </ul>
        </div>
      </div>
    </section>
  );
}
